import React, { useState } from 'react'
import './CartPage.css'
import { useSelector } from 'react-redux'
import { Row,Alert,Container,Col,Table } from 'react-bootstrap';
import { useDecreaseCartProductMutation, useIncreaseCartProductMutation, useRemoveFromCartMutation } from '../services/appApi';
import {loadStripe} from '@stripe/stripe-js';
import {Elements} from '@stripe/react-stripe-js';
import CheckoutForm from '../components/CheckoutForm';

const stripePromise=loadStripe("pk_test_51N5OJrSCMJ09MRJvvS0lpi1Bjug4kafWvYNHZMMaBksHMOwUgfKRjo6rBBiYkXwlIBdXXb7yLWDzmIXgrsKkVSow00BnCovcSX");



function CartPage() {
    const user=useSelector(state=>state.user);
    const products=useSelector(state=>state.products);
    const userCartObj=user.cart;
    // console.log(products);
    // console.log(userCartObj);
    const [increaseCart]=useIncreaseCartProductMutation();
    const [decreaseCart]=useDecreaseCartProductMutation();
    const [removeFromCart,{isLoading}]=useRemoveFromCartMutation();

    function handleDecrease(product){
      const quantity=user.cart[product.productId];
      if(quantity<=0) return alert("Can't proceed");
      decreaseCart(product);
    }

    let cart=products.filter((product)=>userCartObj[product._id]!=null);
    console.log(user);
  return <Container style={{minHeight:'95vh'}} className="cart-container">
    <Row>
        <Col>
        <h1 className='pt-2 h3'>Shopping cart</h1>
        {cart.length===0?(
            <Alert variant='info'>Shopping cart is empty. Add products to your cart</Alert>
        ): <Elements stripe={stripePromise}>
          <CheckoutForm />
        </Elements> }
        </Col>

          {cart.length>0 && (
             <Col md={5}>
            <>
              <Table responsive="sm" className="cart-table">
                <thead>
                  <tr>
                    <th>&nbsp;</th>
                    <th>Product</th>
                    <th>Price</th>
                    <th>Quantity</th>
                    <th>Subtotal</th>
                  </tr>
                </thead>
                <tbody>
                  {cart.map(item=>(
                    <tr>
                      <td>&nbsp;</td>
                      <td>
                        {!isLoading && <i className='fa fa-times' style={{marginRight:10,cursor:"pointer"}} onClick={()=>removeFromCart({userId:user._id,productId:item._id,price:item.price})}></i> }
                        <img src={item.pictures[0].url} style={{width:100,height:100,objectFit:"cover"}} />
                      </td>
                      <td>
                        ${item.price}
                      </td>
                      <td>
                        <span className='quantity-indicator'>
                          <i className='fa fa-minus-circle' onClick={()=>handleDecrease({productId:item._id,price:item.price,userId:user._id})}></i>
                          <span>{user.cart[item._id]}</span>
                          <i className='fa fa-plus-circle' onClick={()=>increaseCart({productId:item._id,price:item.price,userId:user._id})}></i>
                        </span>
                      </td>
                      <td>
                        ${item.price*user.cart[item._id]}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
              <div>
                <h3 className='h4 pt-4'>Total: ${user.cart.total}</h3>
              </div>
            </>
        </Col>
          )}
    </Row>
  </Container>
}

export default CartPage
